import React, { Component } from "react";
import ReactDOM from "react-dom";


function IdFetch() {
  fetch('https://recruitment.hal.skygate.io/companies')
  .then(data => data.json()).then(resp => {
      resp.forEach(company => {
          return {id: company.id,name:company.name,city:company.city};
      });
  });
};
function IncomesFetch(){
  let dataFetch=this.IdFetch();

  fetch('https://recruitment.hal.skygate.io/incomes/' + dataFetch.id).then(data => data.json()).then(response => {
    let income = Math.round(response.incomes.reduce((a, b) => a + parseFloat(b.value), 0) * 100) / 100;
    let lastMonth = Math.round(response.incomes.map((id) => parseFloat(id.date.substring(5, 7)) === parseFloat(new Date().getMonth() + 1) ? id.value : 0).reduce((a, b) => {
        return a + parseFloat(b);
    }, 0) * 100) / 100;
    this.companyDate.push([dataFetch.id, dataFetch.name, dataFetch.city, income, Math.round((income / 49) * 100) / 100, Math.round(response.incomes.map((id) => parseFloat(id.date.substring(5, 7)) === parseFloat(new Date().getMonth() + 1) ? id.value : 0).reduce((a, b) => {
      return a + parseFloat(b);
  }, 0) * 100) / 100 ]);
});
};


class App extends Component {
  constructor() {
    super();
    this.state = { date: []};
  }
  render() {
    return (
      <div>
        <ul>
          {this.companyDate.Length} dfgd
        </ul>
      </div>
    );
  }
}
export default App;
