import React, { Component } from "react";
import ReactDOM from "react-dom";
class App extends Component {
  constructor() {
    super();
    this.state = { companyDate: []};
  }
  presentMonth = parseFloat(new Date().getMonth() + 1);

  componentDidMount() {
    fetch('https://recruitment.hal.skygate.io/companies')
    .then(data => data.json()).then(resp => {
        resp.forEach(company => {
            let id = company.id;
            let name = company.name;
            let city = company.city;
            fetch('https://recruitment.hal.skygate.io/incomes/' + id).then(data => data.json()).then(response => {
                let income = Math.round(response.incomes.reduce((a, b) => a + parseFloat(b.value), 0) * 100) / 100;
                let average = Math.round((income / 49) * 100) / 100;
                let lastMonth = Math.round(response.incomes.map((id) => parseFloat(id.date.substring(5, 7)) === 12 ? id.value : 0).reduce((a, b) => {
                    return a + parseFloat(b);
                }, 0) * 100) / 100;
                this.setState(companyDate.push({ id, name, city, income, average, lastMonth }));
            });
        });
    });
  }
  render() {
    return (
      <div>
        <ul>
          {this.state.data.map(el => (
            <li>
              {el.name}: {el.price_usd}
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
export default App;
