import React, { Component } from "react";
import ReactDOM from "react-dom";
class App extends Component {
  constructor() {
    super();
    this.state = { date: []};
  }

  IdFetch() {
    fetch('https://recruitment.hal.skygate.io/companies')
    .then(data => data.json()).then(resp => {
        resp.forEach(company => {
            return {id: company.id,name:company.name,city:company.city};
        });
    });
  }
  IncomesFetch(){
    let dataFetch=this.IdFetch();
    let companyDate
    fetch('https://recruitment.hal.skygate.io/incomes/' + dataFetch.id).then(data => data.json()).then(response => {
      let presentMonth = parseFloat(new Date().getMonth() + 1);
      let income = Math.round(response.incomes.reduce((a, b) => a + parseFloat(b.value), 0) * 100) / 100;
      let average = Math.round((income / 49) * 100) / 100;
      let lastMonth = Math.round(response.incomes.map((id) => parseFloat(id.date.substring(5, 7)) === presentMonth ? id.value : 0).reduce((a, b) => {
          return a + parseFloat(b);
      }, 0) * 100) / 100;
      companyDate.push({ id, name, city, income, average, lastMonth });
  });
  }


  render() {
    return (
      <div>
        <ul>
          {this.state.data.map(el => (
            <li>
              {el.name}: {el.price_usd}
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
export default App;
