import React, { Component } from "react";
import ReactDOM from "react-dom";


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: '',
      submit: '',
      inputValue: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({
      input: event.target.value
    });
  }
  handleSubmit(event) {
    // change code below this line
    this.setState({
      submit: this.state.input
    });
    // change code above this line
  }
  render() {
    return (
      <div>
        {/*<form onSubmit={this.handleSubmit}>
          <input value={this.state.input} onChange={this.handleChange.bind(this)}/>
          <button type='submit'>Submit!</button>
        </form>
        <p>{this.state.submit}</p>

        <Navbar names={this.state.input}/>*/ }

        <GetInput input2={this.state.inputValue}
          handleChange={this.handleChange}/>
      <RenderInput input2={this.state.inputValue}/>
      </div>
    );
  }
};

class Navbar extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
    <div>
      <h1>Hello, my name is: {this.props.names} </h1>
    </div>
    );
  }
};

class GetInput extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div>
        <h3>Get Input:</h3>
        <input
          value={this.props.input2}
          onChange={this.props.handleChange}/>
      </div>
    );
  }
};

class RenderInput extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div>
        <h3>Input Render:</h3>
        <p>{this.props.input2}</p>
      </div>
    );
  }
};

export default App;
